Verilog<pre><code>module add_1(out, cout, in_0, in_1, cin);
    input   in_0, in_1, cin;
    output  out, cout;
    wire w1, w2, w3, w4;
    xor ( w1, in_0, in_1),
        ( out, w1, cin);
    and ( w2, in_0, in_1),
        ( w3, in_0, cin),
        ( w4, in_1, cin);
    or  ( cout, w2, w3, w4);
endmodule
