C<pre><code>#include &lt;stdio.h&gt;
#include &lt;stdlib.h&gt;

void printHouxu(char n[], int L[], int R[], int i);
int main() {
    int numV;
    scanf("%d", &amp;numV);
    int L[numV], R[numV];
    char n[numV];
    for (int i = 0; i &lt; numV; i++) {
        L[i] = 0;
        R[i] = 0;
    }
    struct element{
        char data;
        int pos[2];
    }elem[numV];

    for (int i = 0; i &lt; numV; i++){
        elem[i].data = '0';
        elem[i].pos[0] = 0;
        elem[i].pos[1] = 0;
    }
    for (int i = 0; i &lt; (numV - 1) / 2; i++) {
        scanf(" %c %d %d", &amp;elem[i].data, &amp;elem[i].pos[0], &amp;elem[i].pos[1]);
        n[i] = elem[i].data;
        L[i] = elem[i].pos[1];
        R[i] = elem[i].pos[0];
    }
    for (int i = (numV - 1) / 2; i &lt; numV; i++) {
        scanf(" %c", &amp;elem[i].data);
        n[i] = elem[i].data;
    }
    printHouxu(n, L, R, 0);
    return 0;
}

void printHouxu(char n[], int L[], int R[], int i) {
